import React, { useState } from 'react';

const BottomBar = () => {
  const [isVisible, setIsVisible] = useState(true);

  const toggleVisibility = () => {
    setIsVisible(!isVisible);
  };

  return (
    <div>
      <button
        className="bottom-bar-toggle"
        onClick={toggleVisibility}
        style={{ color: 'black', backgroundColor: 'transparent', border: 'none' }}
      >
        {isVisible ? 'Ocultar Barra' : 'Mostrar Barra'}
      </button>
      <div className={`bottom-bar ${isVisible ? 'visible' : ''}`}>
        <button className="bottom-bar-button">Botón 1</button>
        <button className="bottom-bar-button">Botón 2</button>
        <button className="bottom-bar-button">Botón 3</button>
      </div>
    </div>
  );
};

export default BottomBar;
