import React, { useState } from 'react';

const Prompt = () => {
  const [showButtons, setShowButtons] = useState(false);

  const handlePromptClick = () => {
    setShowButtons(true);
  };

  const handleButtonClick = (buttonText) => {
    console.log(`Se hizo clic en el botón ${buttonText}`);
    // Aquí puedes realizar las acciones correspondientes a cada botón
  };

  return (
    <div>
      <button className="btn btn-primary" onClick={handlePromptClick}>
        Mostrar opciones
      </button>
      {showButtons && (
        <div>
          <button className="btn btn-secondary" onClick={() => handleButtonClick('Opción 1')}>
            Opción 1
          </button>
          <button className="btn btn-secondary" onClick={() => handleButtonClick('Opción 2')}>
            Opción 2
          </button>
          <button className="btn btn-secondary" onClick={() => handleButtonClick('Opción 3')}>
            Opción 3
          </button>
        </div>
      )}
    </div>
  );
};

export default Prompt;
