import React, { useState } from 'react';
import SettingsButton from './SettingsButton';
import StartMessage from './StartMessage';

import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [showMessage, setShowMessage] = useState(false);

  const handleButtonClick = () => {
    setShowMessage(!showMessage); // Invierte el valor actual de showMessage
  };

  const containerStyle = {
    position:'relative' ,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundImage: `url("https://www.neuqueninforma.gob.ar/wp-content/uploads/2022/03/web-Museo-Bellas-Artes_DSC_5690_019.jpg")`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',// Opacidad del fondo
    filter: 'brightness(80%)',
  };
  const overlayStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: -1, // Fondo negro semitransparente
  };
  return (
    <div style={containerStyle}>
      {showMessage && <StartMessage />}
      <SettingsButton onClick={handleButtonClick} />
      <div style={overlayStyle}></div>
    </div>

  );
}

export default App;
