import React from 'react';

function Home() {
    const containerStyle = {
        position: 'relative',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundImage: `url("https://www.rionegro.com.ar/wp-content/uploads/2023/04/museo-1.jpg")`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',// Opacidad del fondo
        filter: 'brightness(80%)',
    };
    const overlayStyle = {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundColor: 'rgba(0, 0, 0, 0.2)',
        zIndex: -1, // Fondo negro transparente
    };
    return (
        <div style={containerStyle}>
            <div className="button-container">
                <div className="background-square"></div>
                <div className="button-text">
                    <p className='text'>M N B A</p>
                    <p className='subText'>Ingresar a nuestra experiencia virtual</p>
                </div>
                <button className="btn custom-button" onClick={onClick} style={{ backgroundColor: '#ebebeb', borderRadius: '0', width: '200px' }}>
                    Comenzar
                </button>
            </div>
            <div style={overlayStyle}></div>
        </div>
    );
}

export default Home;
